import axios from "axios"
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom"
import styled from "styled-components";
import { handleImgError } from "../../lib/util";

const Place_Img = styled.img`
    border-radius: 10px;
    width: 400px;
    height: 400px;
    margin-top: 30px;
    @media screen and (max-width: 1200px) {
        margin: 0 auto;
        width: 300px;
        height: 300px;
    }
    @media screen and (max-width: 700px) {
        margin: 0 auto;
        width: 200px;
        height: 200px;
    }
`

export default function PlaceImg() {
    const { id } = useParams();
    const [imgURL, setImgURL] = useState("");
    const getPlaceImg = async () => {
        await axios.get(`/placeDetail/${id}`)
            .then((res) => {
                setImgURL(res.data.restaurant.img);
            })
    };

    useEffect(() => {
        getPlaceImg();
    }, []);
    return (
        <Place_Img src={imgURL} alt="준비중" onError={handleImgError} />
    )
}

