import { AnimatePresence, motion, Variants } from "framer-motion"
import { useState, useEffect } from "react";
import styled from "styled-components";
import axios from "axios";

const Container = styled.div`
    position: relative;
    margin: 0 auto;
    margin-bottom: 100px;
    background-color: black;
    height: 300px;
    width: 1200px;
    overflow: hidden;
`

const Slider = styled(motion.div)`
  display: grid;
  gap: 10px;
  grid-template-columns: repeat(4, 1fr);
  position: absolute;
  width: 100%;
  top: 50px;
`

const PlaceBox = styled(motion.img)`
    background-color: white;
    width: 100%;
    height: 200px;
    background-size: cover;
    background-position: center center;
    color: black;
`

const NextBtn = styled.button`
    position: absolute;
    right: 0;
    top: 50%;
`

const PrevBtn = styled.button`
    position: absolute;
    top: 50%;
`

const SliderVar: Variants = {
    hidden: (isBack: boolean) => ({
        x: isBack ? -1200 - 10 : 1200 + 10,
    }),
    visible: {
        x: 0,
    },
    exit: (isBack: boolean) => ({
        x: isBack ? 1200 + 10 : -1200 - 10,
    }),
};

export default function WholePlace() {
    const [images, setImages] = useState<string[]>([]);
    const [Index, setIndex] = useState<number>(0);
    const [back, setBack] = useState<boolean>(false);
    const [leaving, setLeaving] = useState<boolean>(false);

    useEffect(() => {
        const fetchImages = async () => {
            try {
                const response = await axios.get<{ images: string[] }>('/wholeplace'); // Change the URL to your server endpoint
                setImages(response.data.images);
            } catch (error) {
                console.error('Error fetching images:', error);
            }
        };

        fetchImages();
    }, []);

    const nextPlease = async () => {
        if(leaving) return;
        toggleLeaving();
        await setBack(false);
        setIndex((prev) => (prev === images.length - 4 ? 0 : prev + 4));
    }

    const prevPlease = async () => {
        if(leaving) return;
        toggleLeaving();
        await setBack(true);
        setIndex((prev) => (prev === 0 ? images.length - 4 : prev - 4));
    }

    const toggleLeaving = () => setLeaving((cur) => !cur);

    return (
        <Container>
            <AnimatePresence initial={false} onExitComplete={toggleLeaving}>
                <Slider
                    custom={back}
                    variants={SliderVar}
                    initial="hidden"
                    animate="visible"
                    exit="exit"
                    transition={{ type: "tween", duration: 1 }}
                    key={Index}
                >
                    {images.slice(Index, Index + 4).map((imageUrl, idx) => (
                        <PlaceBox src={imageUrl} key={idx} />
                    ))}
                </Slider>
                <PrevBtn onClick={prevPlease}>prev</PrevBtn>
                <NextBtn onClick={nextPlease}>next</NextBtn>
            </AnimatePresence>
        </Container>
    )
}
