import { AnimatePresence, motion } from "framer-motion";
import { useState, useEffect } from "react";
import styled from "styled-components";
import axios from "axios";

const Container = styled.div`
    position: relative;
    margin: 0 auto;
    margin-bottom: 100px;
    background-color: black;
    height: 300px;
    width: 1200px;
    overflow: hidden;
`;

const Slider = styled(motion.div)`
    display: grid;
    gap: 10px;
    grid-template-columns: repeat(4, 1fr);
    position: absolute;
    width: 100%;
    top: 50px;
`;

const PlaceBox = styled(motion.img)`
    background-color: white;
    width: 100%;
    height: 200px;
    background-size: cover;
    background-position: center center;
    color: black;
`;

const NextBtn = styled.button`
    position: absolute;
    right: 0;
    top: 50%;
`;

const PrevBtn = styled.button`
    position: absolute;
    top: 50%;
`;

const SliderVar = {
    hidden: (isBack: boolean) => ({
        x: isBack ? -1200 - 10 : 1200 + 10,
    }),
    visible: {
        x: 0,
    },
    exit: (isBack: boolean) => ({
        x: isBack ? 1200 + 10 : -1200 - 10,
    }),
};

export default function WholePlace() {
    const [images, setImages] = useState([]);
    const [Index, setIndex] = useState(0);
    const [back, setBack] = useState(false);
    const [leaving, setLeaving] = useState(false);

    useEffect(() => {
        const fetchImages = async () => {
            try {
                const response = await axios.get('/'); // 서버에서 이미지 URL 가져오기
                setImages(response.data); // 이미지 URL 설정
            } catch (error) {
                console.error('Error fetching images:', error);
            }
        };
        fetchImages();
    }, []);

    const nextPlease = () => {
        if (leaving) return;
        toggleLeaving();
        setBack(false);
        setIndex((prev) => (prev === 16 ? 0 : prev + 4));
    };

    const prevPlease = () => {
        if (leaving) return;
        toggleLeaving();
        setBack(true);
        setIndex((prev) => (prev === 0 ? 16 : prev - 4));
    };

    const toggleLeaving = () => setLeaving((cur) => !cur);

    return (
        <Container>
            <AnimatePresence initial={false} onExitComplete={toggleLeaving}>
                <Slider
                    custom={back}
                    variants={SliderVar}
                    initial="hidden"
                    animate="visible"
                    exit="exit"
                    transition={{ type: "tween", duration: 1 }}
                    key={Index}
                >
                    {images.slice(Index, Index + 4).map((imageUrl, idx) => (
                        <PlaceBox src={imageUrl} key={idx} />
                    ))}
                </Slider>
                <PrevBtn onClick={prevPlease}>prev</PrevBtn>
                <NextBtn onClick={nextPlease}>next</NextBtn>
            </AnimatePresence>
        </Container>
    );
}
