import axios from 'axios';
import React, { useState, ChangeEvent, FormEvent, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { motion } from "framer-motion";
import styled from "styled-components";

interface FormData {
    id: string;
    password: string;
}

const LoginContainer = styled(motion.div)`
    position: absolute;
    z-index: 1;
    top: 200px;
    left: 0;
    right: 0;
    margin: 0 auto;
    width: 400px;
    height: 400px; /* Increased height to accommodate the search section */
    background-color: rgba(255, 255, 255, 1);
    border-radius: 40px;
    box-shadow: 0 2px 3px rgba(0, 0, 0, 0.1), 0 10px 20px rgba(0, 0, 0, 0.06);
    text-align: center;
`;

const SearchContainer = styled.div`
    margin-top: 20px; /* Increased margin for better spacing */
`;

const SessionExpirationInfo = styled.div`
    margin-top: 10px;
    font-size: 14px;
`;

const boxVariants = {
    initial: {
        opacity: 0,
        scale: 0
    },
    visible: {
        opacity: 1,
        scale: 1
    },
    leaving: {
        opacity: 0,
        scale: 0
    }
};

interface MapProps {
    center: { x: number; y: number };
    searchResult: { x: number; y: number } | null;
}

declare global {
    interface Window {
        kakao: any;
    }
}


const MapContainer = ({ center, searchResult }: MapProps) => {
    useEffect(() => {
        if (searchResult) {
            const script = document.createElement('script');
            script.src = `//dapi.kakao.com/v2/maps/sdk.js?appkey=9a2e1c8b0bd726e6522013b770aef13f&libraries=services&autoload=false`;
            script.async = true;
            document.body.appendChild(script);

            script.onload = () => {
                window.kakao.maps.load(() => {
                    const container = document.getElementById('map');
                    const options = {
                        center: new window.kakao.maps.LatLng(center.y, center.x),
                        level: 5
                    };
                    const map = new window.kakao.maps.Map(container, options);

                    if (searchResult) {
                        const markerPosition = new window.kakao.maps.LatLng(searchResult.y, searchResult.x);
                        const marker = new window.kakao.maps.Marker({
                            position: markerPosition
                        });
                        marker.setMap(map);
                    }
                });
            };

            return () => {
                document.body.removeChild(script);
            };
        }
    }, [center, searchResult]);

    return <div id="map" style={{ width: '100%', height: '100%' }} />;
};

const LoginBox = () => {
    const [formData, setFormData] = useState<FormData>({ id: '', password: '' });
    const [sessionID, setSessionID] = useState<string>('');
    const [userName, setUserName] = useState<string>('');
    const [searchTerm, setSearchTerm] = useState<string>('');
    const [searchResult, setSearchResult] = useState<{ x: number; y: number } | null>(null);
    const [userLocation, setUserLocation] = useState<{ x: number; y: number } | null>(null);
    const navigate = useNavigate();
    const [sessionExpiration, setSessionExpiration] = useState<Date | null>(null);

    useEffect(() => {
        const loggedInSessionID = sessionStorage.getItem('sessionID');
        const loggedInUserName = sessionStorage.getItem('userName');
        if (loggedInSessionID) {
            setSessionID(loggedInSessionID);
        }
        if (loggedInUserName) {
            setUserName(loggedInUserName);
        }
    }, []);

    useEffect(() => {
        if (sessionID) {
            const expiration = new Date();
            expiration.setMilliseconds(expiration.getMilliseconds() + 60000);
            setSessionExpiration(expiration);

            const timer = setTimeout(() => {
                handleLogout();
            }, 60000);

            return () => clearTimeout(timer);
        }
    }, [sessionID]);

    const handleSubmit = async (e: FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        try {
            const response = await axios.post('/login', formData);
            const { sessionID, userName } = response.data;
            if (response.data.message === '로그인 성공' && sessionID) {
                setSessionID(sessionID);
                setUserName(userName);
                sessionStorage.setItem('sessionID', sessionID);
                sessionStorage.setItem('userName', userName);
            } else {
                console.error('로그인 실패:', response.data.message);
            }
        } catch (error) {
            console.error('로그인 중 오류가 발생했습니다:', error);
        }
    };

    const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSearch = async () => {
        try {
            const response = await axios.get(`https://dapi.kakao.com/v2/local/search/keyword.json?query=${searchTerm}`, {
                headers: {
                    Authorization: "KakaoAK f1a6ff5fce786c3d0407226bb3e8ec57"
                }
            });
            if (response.data.documents && response.data.documents.length > 0) {
                // Filter out specific locations such as '의정부 시청'
                const filteredResults = response.data.documents.filter((result: any) => !result.place_name.includes('의정부 시청'));
                // Check if there are any remaining results after filtering
                if (filteredResults.length > 0) {
                    const { x, y } = filteredResults[0];
                    setSearchResult({ x, y });
                } else {
                    console.error('검색 결과를 찾을 수 없습니다.');
                }
            } else {
                console.error('검색 결과를 찾을 수 없습니다.');
            }
        } catch (error) {
            console.error('검색 중 오류가 발생했습니다:', error);
        }
    };

    const handleGetUserLocation = () => {
        navigator.geolocation.getCurrentPosition((position) => {
            const { latitude, longitude } = position.coords;
            setUserLocation({ x: longitude, y: latitude });
        });
    };

    const handleLogout = async () => {
        try {
            await axios.get('/logout');
            sessionStorage.removeItem('sessionID');
            setSessionID('');
            setUserName('');
            navigate('/');
        } catch (error) {
            console.error('로그아웃 중 오류가 발생했습니다:', error);
        }
    };

    return (
        <LoginContainer
            variants={boxVariants}
            initial="initial"
            animate="visible"
            exit="leaving"
        >
            <form onSubmit={handleSubmit}>
                <label>
                    아이디:
                    <input
                        type="text"
                        name="id"
                        value={formData.id}
                        onChange={handleChange}
                    />
                </label>
                <br />
                <label>
                    비밀번호:
                    <input
                        type="password"
                        name="password"
                        value={formData.password}
                        onChange={handleChange}
                    />
                </label>
                <br />
                <button type="submit">로그인</button>
            </form>

            {sessionID && (
                <>
                    <p>안녕하세요, {userName}님</p> 
                    <button onClick={handleLogout}>로그아웃</button>
                </>
            )}
            {sessionExpiration && (
                <SessionExpirationInfo>
                    세션 만료 시간: {sessionExpiration.toLocaleTimeString()}까지
                </SessionExpirationInfo>
            )}

            <SearchContainer>
                <input
                    type="text"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
                <button onClick={handleSearch}>지역 검색</button>
                <button onClick={handleGetUserLocation}>내 위치 가져오기</button>
                {searchResult && (
                    <>
                        <p>검색된 위치: x={searchResult.x}, y={searchResult.y}</p>
                        <MapContainer
                            center={searchResult}
                            searchResult={searchResult}
                        />
                    </>
                )}
                {userLocation && (
                    <p>사용자 위치: x={userLocation.x}, y={userLocation.y}</p>
                )}
            </SearchContainer>
        </LoginContainer>
    );
};

export default LoginBox;
