import axios from "axios";
import { motion } from "framer-motion";
import { useEffect, useState } from "react";
import styled from "styled-components";

const BoxContainer = styled.div`
    display: flex;
    flex-direction: column;
    background-color: black;
    color: white;
    border-radius: 15px;
    height: 100%;
    width: 55%;
    margin: 0 auto;
    margin-bottom: 100px;
    @media screen and (max-width: 900px) {
        width: 90%;
    }
`;

const PlaceContainer = styled.div`
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    margin: 0 auto;
`;

const PlaceBox = styled(motion.img)`
    margin: 30px;
    background-color: white;
    width: 300px;
    height: 300px;
    background-size: cover;
    background-position: center center;
    color: black;
    @media screen and (max-width: 700px){
    }
`;

const ReviewContainer = styled.div`
    border: solid 1px white;
    border-radius: 5px;
    margin: 20px;
    font-size: 30px;
    height: 100%;
`;

const Title = styled.div`
    font-size: 30px;
    margin: 20px;
`;

const Rating = styled.div`
    margin: 10px;
`;

const Comment = styled.div`
    margin: 10px;
`;

function MyPage() {
    const [searchTerm, setSearchTerm] = useState<string>('');
    const [restaurantData, setRestaurantData] = useState<any[]>([]);
    const [sessionID, setSessionID] = useState<string>('');
    const [userAddress, setUserAddress] = useState<string>('');
    const [userLocation, setUserLocation] = useState<{ x: number, y: number } | null>(null);

    const handleSearch = async () => {
        try {
            const response = await axios.get(`https://dapi.kakao.com/v2/local/search/keyword.json?query=${searchTerm}`, {
                headers: {
                    Authorization: "KakaoAK f1a6ff5fce786c3d0407226bb3e8ec57"
                }
            });
            if (response.data.documents && response.data.documents.length > 0) {
                const { address_name } = response.data.documents[0];
                setUserAddress(address_name);
                console.log('검색된 주소:', address_name);
            } else {
                console.error('검색 결과를 찾을 수 없습니다.');
            }
        } catch (error) {
            console.error('검색 중 오류가 발생했습니다:', error);
        }
    };

    const handleGetUserLocation = () => {
        navigator.geolocation.getCurrentPosition(async (position) => {
            const { latitude, longitude } = position.coords;
            setUserLocation({ x: longitude, y: latitude });
            const address = await getAddressFromCoordinates(latitude, longitude);
            if (address) {
                setUserAddress(address);
                console.log('사용자 위치:', address);
            }
        });
    };

    const getAddressFromCoordinates = async (latitude: number, longitude: number) => {
        try {
            const response = await axios.get(`https://dapi.kakao.com/v2/local/geo/coord2address.json?x=${longitude}&y=${latitude}`, {
                headers: {
                    Authorization: "KakaoAK f1a6ff5fce786c3d0407226bb3e8ec57"
                }
            });
            if (response.data.documents && response.data.documents.length > 0) {
                const address = response.data.documents[0].address.address_name;
                return address;
            } else {
                console.error('주소를 찾을 수 없습니다.');
                return null;
            }
        } catch (error) {
            console.error('주소를 가져오는 중 오류가 발생했습니다:', error);
            return null;
        }
    };

    useEffect(() => {
        const loggedInSessionID = sessionStorage.getItem('sessionID') || ''; // 세션 스토리지에서 세션 아이디 가져오기
        setSessionID(loggedInSessionID);
    }, []);

    return (
        <BoxContainer>
            <Title>카카오로 주소 검색</Title>
            <div>
                <input
                    type="text"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    placeholder="주소를 입력하세요."
                />
                <button onClick={handleSearch}>검색</button>
            </div>
            <Title>사용자 위치 가져오기</Title>
            <button onClick={handleGetUserLocation}>사용자 위치 가져오기</button>
            <Title>즐겨 찾는 식당</Title>
            <PlaceContainer>
                {restaurantData.map((restaurant, index) => (
                    <PlaceBox key={index} src={restaurant.image_url} />
                ))}
            </PlaceContainer>
            <Title>나의 리뷰</Title>
            {["리뷰 1", "리뷰 2", "리뷰 3", "리뷰 4"].map((comment, index) => (
                <ReviewContainer key={index}>
                    <Rating>&#9733; 4.3</Rating>
                    <Comment>{comment}</Comment>
                </ReviewContainer>
            ))}
        </BoxContainer>
    );
}

export default MyPage;
