import axios from 'axios';
import React, { useState, ChangeEvent, FormEvent } from 'react';
import { motion } from "framer-motion";
import styled from "styled-components";
import bcrypt from 'bcrypt';


interface FormData {
    id: string;
    password: string;
}

const LoginContainer = styled(motion.div)`
    position: absolute;
    z-index: 1;
    top: 200px;
    left: 0;
    right: 0;
    margin: 0 auto;
    width: 400px;
    height: 200px;
    background-color: rgba(255, 255, 255, 1);
    border-radius: 40px;
    box-shadow: 0 2px 3px rgba(0, 0, 0, 0.1), 0 10px 20px rgba(0, 0, 0, 0.06);
    text-align: center;
`;

const boxVariants = {
    initial: {
        opacity: 0,
        scale: 0
    },
    visible: {
        opacity: 1,
        scale: 1
    },
    leaving: {
        opacity: 0,
        scale: 0
    }
};

export default function LoginBox() {
    const [formData, setFormData] = useState<FormData>({ id: '', password: '' });
    const [loggedInUser, setLoggedInUser] = useState<string | null>(null);
    
    const handleSubmit = async (e: FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    try {
        const hashedPassword = await bcrypt.hash(formData.password, 10); // 비밀번호를 해싱
        console.log("전송되는 데이터:", { id: formData.id, password: hashedPassword }); // 데이터 콘솔 출력
        const { data } = await axios.post('/login', {
            id: formData.id,
            password: hashedPassword // 해싱된 비밀번호 전송
        });
        console.log(data);
        setLoggedInUser(data.username);
    } catch (error) {
        console.error('로그인 중 오류가 발생했습니다:', error);
    }
};

    const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    return (
        <LoginContainer
            variants={boxVariants}
            initial="initial"
            animate="visible"
            exit="leaving"
        >   
            {!loggedInUser ? (
                <form onSubmit={handleSubmit}>
                    <label>
                        아이디:
                        <input
                            type="text"
                            name="id"
                            value={formData.id}
                            onChange={handleChange}
                        />
                    </label>
                    <br />
                    <label>
                        비밀번호:
                        <input
                            type="password"
                            name="password"
                            value={formData.password}
                            onChange={handleChange}
                        />
                    </label>
                    <br />
                    <button type="submit">로그인</button>
                </form>
            ) : (
                <p>환영합니다, {loggedInUser}님!</p>
            )}
        </LoginContainer>
    );
}
